import express from "express";
import { getAllUsers, getAUser, loginUser, myProfile, updateName, verifySignupUser, signupUser, resendOTP, forgotPassword, verifyForgotOTP, resetPassword, resendForgotOTP } from '../controlers/user.js';
import { isAuth } from "../middleware/isAuth.js";
const router = express.Router();
router.post("/signup", signupUser);
router.post("/login", loginUser);
router.post("/verify", verifySignupUser);
router.post("/resendotp", resendOTP);
router.get("/me", isAuth, myProfile);
router.get("/user/all", isAuth, getAllUsers);
router.get("/user/:id", getAUser);
router.post("/update/user", isAuth, updateName);
router.post("/forgotpassword", forgotPassword);
router.post("/verifyforgototp", verifyForgotOTP);
router.post("/resetpassword", resetPassword);
router.post("/resendforgotpassotp", resendForgotOTP);
export default router;
